// Include the V5 Library
#include "vex.h"
  
// Allows for easier use of the VEX Library
using namespace vex;



int main() {
  // Initializing Robot Configuration. DO NOT REMOVE!
  vexcodeInit();
  // Begin project code
  int accelerationValue;
 
   Motor11.setMaxTorque(50,percent);
 
  
  while(true)
  {
    accelerationValue = Controller1.Axis3.position();
    Motor11.setVelocity(accelerationValue,percent);
    //assigns axis3 position to int variable and sets that as the motor velocity
  
   if(accelerationValue != 0)
  {
    Motor11.spin(forward);
  }
  //if the controller axis isnt centered then rotate
  else {
    Motor11.stop();
  }
 
 
 
   wait(20,msec);
   //small delay 
 }


 



}
